<?php require_once '../../inc/db_connect.php';$output = array('success' => false, 'messages' => array());$memberId = $_POST['member_id'];$sql = "DELETE from lingkup_materi where id_lm= {$memberId}";$query = $connect->query($sql);if($query === TRUE) {	$output['success'] = true;	$output['messages'] = 'Lingkup Materi Berhasil dihapus';} else {	$output['success'] = false;	$output['messages'] = 'Error saat mencoba menghapus Lingkup Materi';}// close database connection$connect->close();echo json_encode($output);