?><script>$("#tpTable").DataTable({pageLength:5,lengthMenu:[[5,10,20],[5,10,20]],autoWidth:!1});		$("#buatmateri").unbind('submit').bind('submit', function() {			var form = $(this);			//submi the form to server			$.ajax({				url : form.attr('action'),				type : form.attr('method'),				data : form.serialize(),				dataType : 'json',				success:function(response) {					if(response.success == true) {						$("#modalmateri").modal('hide');						Codebase.helpers('jq-notify', {							align: 'center',             // 'right', 'left', 'center'							from: 'bottom',                // 'top', 'bottom'							type: 'success',               // 'info', 'success', 'warning', 'danger'							icon: 'fa fa-info me-5',    // Icon class							message: response.messages						});						//swal(response.messages, {buttons: false,timer: 2000,});						var kelas = $('#kelas').val();						var smt = $('#smt').val();						var mp = $('#mp').val();						$("#tpTable").DataTable({						  pageLength: 5,						  lengthMenu: [							 [5, 10, 20],							 [5, 10, 20],						  ],						  autoWidth: !1,						  responsive: !0,						  "destroy":true,						  "searching": true,						  "paging":true,						  "ajax": "modul/materi/lm.php?kelas="+kelas+"&smt="+smt+"&tapel="+tapel+"&mp="+mp						});						//$("#createKDFormk")[0].reset();						// this function is built in function of datatables;					} else {						Codebase.helpers('jq-notify', {							align: 'center',             // 'right', 'left', 'center'							from: 'bottom',                // 'top', 'bottom'							type: 'success',               // 'info', 'success', 'warning', 'danger'							icon: 'fa fa-info me-5',    // Icon class							message: response.messages						});					}  // /else				} // success  			}); // ajax subit 							return false;		}); // /submit form for Tujuan		$('#modalkesehatan').on('show.bs.modal', function (e) {            var rowid = $(e.relatedTarget).data('pdid');			var rowsmt = $(e.relatedTarget).data('smt');			var rowtapel = $(e.relatedTarget).data('tapel');			var rowkelas = $(e.relatedTarget).data('kelas');            //menggunakan fungsi ajax untuk pengambilan data            $.ajax({                type : 'post',                url : 'modul/rapor/modal_siswa.php',                data :  'rowid='+ rowid +'&smt='+rowsmt+'&tapel='+rowtapel+'&kelas='+rowkelas,				beforeSend: function()					{							$(".fetched-data").html('<div class="block block-rounded"><div class="block-content">Loading.....<br/><div class="spinner-grow spinner-grow text-primary" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-secondary" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-success" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-danger" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-warning" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-info" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-light" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-dark" role="status"><span class="sr-only">Loading...</span></div></div></div>');					},                success : function(data){					$('.fetched-data').html(data);//menampilkan data ke dalam modal                }            });        });</script>