<?phpinclude("../../inc/db_connect.php");function TanggalIndo($tanggal){	$bulan = array ('Januari',				'Februari',				'Maret',				'April',				'Mei',				'Juni',				'Juli',				'Agustus',				'September',				'Oktober',				'November',				'Desember'			);	$split = explode('-', $tanggal);	return $split[2] . ' ' . $bulan[ (int)$split[1]-1 ] . ' ' . $split[0];};$kelas=$_POST['kelas'];$tapel=$_POST['tapel'];$smt=$_POST['smt'];$idr=$_POST['rowid'];$tgl=$_POST['tgl'];$bio=$kes=$connect->query("SELECT * FROM siswa WHERE peserta_didik_id='$idr'")->fetch_assoc();$ids=$bio['peserta_didik_id'];$filegbr = 'https://sdi-aljannah.web.id/apins/images/siswa/'.$bio['avatar'];$file_headerss = @get_headers($filegbr);if($file_headerss[0] == 'HTTP/1.1 404 Not Found') {	//$exists = false;	$avatarm="https://sdi-aljannah.web.id/apins/images/siswa/user-default.png";}else {	//$exists = true;	$avatarm='https://sdi-aljannah.web.id/apins/images/siswa/'.$bio['avatar'];};$day = date('D', strtotime($tgl));	$dayList = array(		'Sun' => 'Minggu',		'Mon' => 'Senin',		'Tue' => 'Selasa',		'Wed' => 'Rabu',		'Thu' => 'Kamis',		'Fri' => 'Jumat',		'Sat' => 'Sabtu'	);?>	<?php if($dayList[$day]==="Sabtu" || $dayList[$day]==="Minggu"){ ?>		<div class="block block-rounded">            <div class="block-header block-header-default">              <h3 class="block-title">                <i class="fa fa-share-alt me-1 text-muted"></i> Data Absensi <?=$bio['nama'];?>              </h3>            </div>			<div class="block-content">				<div class="alert alert-info alert-dismissible">					<h4><i class="icon fa fa-info"></i> Error</h4>					Hari <?=$dayList[$day];?> Tidak bisa Absen ya :-)				</div>			</div>		</div>	<?php }else{ ?>	<form class="form" action="modul/rapor/tambahabsen.php" autocomplete="off" method="POST" id="createAbsenForm">				<div class="block block-rounded text-center">					<div class="block-content block-content-full block-content-sm">					  <div class="fw-semibold"><?=$bio['nama'];?></div>					</div>					<div class="block-content block-content-full bg-body-light">					  <img class="img-avatar img-avatar-thumb" src="<?=$avatarm;?>" alt="">					</div>					<div class="block-content block-content-full" id="tampil">						<input type="hidden" name="pdid" class="form-control"  value="<?=$bio['peserta_didik_id'];?>">						<input type="hidden" name="smt" class="form-control"  value="<?=$smt;?>">						<input type="hidden" name="tapel" class="form-control"  value="<?=$tapel;?>">						<input type="hidden" name="kelas" class="form-control"  value="<?=$kelas;?>">						<input type="hidden" name="tanggals" class="form-control"  value="<?=$tgl;?>">						<div class="row mb-4">							<div class="col-12">								<div class="row items-push">									<div class="col-md-4">										<div class="form-check form-block">										  <input type="radio" class="form-check-input" id="example-radio-block1" name="jnabsen" value="S">										  <label class="form-check-label" for="example-radio-block1">											<span class="d-block fw-normal text-center my-3">											  <span class="fs-4 fw-semibold">Sakit</span>											</span>										  </label>										</div>									</div>									<div class="col-md-4">										<div class="form-check form-block">										  <input type="radio" class="form-check-input" id="example-radio-block2" name="jnabsen" value="I">										  <label class="form-check-label" for="example-radio-block2">											<span class="d-block fw-normal text-center my-3">											  <span class="fs-4 fw-semibold">Ijin</span>											</span>										  </label>										</div>									</div>									<div class="col-md-4">										<div class="form-check form-block">										  <input type="radio" class="form-check-input" id="example-radio-block3" name="jnabsen" value="A">										  <label class="form-check-label" for="example-radio-block3">											<span class="d-block fw-normal text-center my-3">											  <span class="fs-4 fw-semibold">Alfa</span>											</span>										  </label>										</div>									</div>								</div>							</div>						</div>						<div class="block-content block-content-full block-content-sm text-end border-top">							<button type="button" class="btn btn-alt-secondary" data-bs-dismiss="modal">Tutup</button>							<button type="submit" class="btn btn-alt-primary">Simpan</button>						</div>					</div>				</div>	</form>	<?php } ?>	<script>		$("#createAbsenForm").unbind('submit').bind('submit', function() {			$(".text-danger").remove();			var form = $(this);			//submi the form to server			$.ajax({				url : form.attr('action'),				type : form.attr('method'),				data : form.serialize(),				dataType : 'json',				beforeSend: function()				{						$("#tampil").html('Menyimpan.....<br/><div class="spinner-grow spinner-grow text-primary" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-secondary" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-success" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-danger" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-warning" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-info" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-light" role="status"><span class="sr-only">Loading...</span></div><div class="spinner-grow spinner-grow text-dark" role="status"><span class="sr-only">Loading...</span></div>');				},				success:function(response) {					// remove the error 					//$(".form-group").removeClass('has-error').removeClass('has-success');					if(response.success == true) {						// reset the form						$("#tambahAbsen").modal('hide');						// reload the datatables						var tabsen=$('#tglabsen').val();						var smt=$('#smt').val();						var tapel=$('#tapel').val();						var kelas=$('#kelas').val();						$.ajax({							type : 'get',							url : 'modul/rapor/cek_absen.php',							data :  'kelas='+kelas+'&tapel='+tapel+'&tgl='+tabsen+'&smt='+smt,							dataType : 'json',							success : function(data){								$('#sakit').html(data.sakit);								$('#ijin').html(data.ijin);								$('#alfa').html(data.alfa);							}						});						absenTable.ajax.reload(null, false);						dataabsen.ajax.reload(null, false);						Codebase.helpers('jq-notify', {							align: 'center',             // 'right', 'left', 'center'							from: 'bottom',                // 'top', 'bottom'							type: 'success',               // 'info', 'success', 'warning', 'danger'							icon: 'fa fa-info me-5',    // Icon class							message: response.messages						});					} else {						Codebase.helpers('jq-notify', {							align: 'center',             // 'right', 'left', 'center'							from: 'bottom',                // 'top', 'bottom'							type: 'danger',               // 'info', 'success', 'warning', 'danger'							icon: 'fa fa-info me-5',    // Icon class							message: response.messages						});					}  // /else				} // success  			}); // ajax subit 							return false;		}); // /submit form for create member	</script>