<?phprequire_once '../../inc/db_connect.php';
$kelas=$_GET['kelas'];$mp=$_GET['mp'];$tapel=$_GET['tapel'];$smt=$_GET['smt'];
$ab=substr($kelas,0,1);if($tapel_aktif==$tapel and $smt_aktif==$smt){	$edit=true;}else{	$edit=false;};$sql = "select * from penempatan where tapel='$tapel' and smt='$smt' and rombel='$kelas' order by nama asc";$query = $connect->query($sql);$mapel=$connect->query("select * from mata_pelajaran where id_mapel='$mp'")->fetch_assoc();if($mp==0){	echo "<div class='alert alert-info alert-dismissible'><h4><i class='icon fa fa-info'></i> Informasi</h4>Silahkan Pilih Mapel</div>";}else{?>	<input type="hidden" class="form-control" id="tapel" name="tapel" value="<?=$tapel;?>">	<input type="hidden" class="form-control" id="smt" name="smt" value="<?=$smt;?>">	<table class="table table-bordered table-striped table-vcenter js-dataTable-responsive" id="tpTable">        <thead>            <tr>                <th></th>				<th>Nama Siswa</th>				<th>Nilai</th>				<th>Deskripsi</th>				<th class="d-none d-sm-table-cell"></th>            </tr>        </thead>        <tbody>			<?php 			while($s=$query->fetch_assoc()) {				$idp=$s['peserta_didik_id'];				$siswa=$connect->query("select * from siswa where peserta_didik_id='$idp'")->fetch_assoc();				$filegbr = 'https://sdi-aljannah.web.id/apins/images/siswa/'.$siswa['avatar'];				$file_headerss = @get_headers($filegbr);				if($file_headerss[0] == 'HTTP/1.1 404 Not Found') {					//$exists = false;					$avatarm="https://sdi-aljannah.web.id/apins/images/siswa/user-default.png";				}else {					//$exists = true;					$avatarm='https://sdi-aljannah.web.id/apins/images/siswa/'.$siswa['avatar'];				};				$sql1 = "select * from raport_ikm where id_pd='$idp' and kelas='$kelas' and smt='$smt' and tapel='$tapel' and mapel='$mp'";				$nh = $connect->query($sql1);				$cekrapor=$connect->query("select * from raport_ikm where id_pd='$idp' and kelas='$kelas' and smt='$smt' and tapel='$tapel' and mapel='$mp'")->num_rows;				if($cekrapor>0){					$m=$nh->fetch_assoc();					$data = explode("|" , $m['deskripsi']);					$kelebihan=$data[0];					$kelemahan=$data[1];					if(empty($m['nilai'])){						$nHar='';					}else{						$nHar=number_format($m['nilai'],0);					};					if($edit){						$nh='						<button class="btn btn-info btn-border btn-round btn-sm" data-kelas="'.$kelas.'" data-mp="'.$mp.'" data-tapel="'.$tapel.'" data-smt="'.$smt.'" data-pdid="'.$idp.'" id="getRaport"><i class="fas fa-sync"></i></button>						';					}else{						$nh=$nHar;					};				}else{					$nHar='';					$kelebihan='';					$kelemahan='';					if($edit){						$nh='						<button class="btn btn-info btn-border btn-round btn-sm" data-kelas="'.$kelas.'" data-mp="'.$mp.'" data-tapel="'.$tapel.'" data-smt="'.$smt.'" data-pdid="'.$idp.'" id="getRaport"><i class="fas fa-sync"></i></button>						';					}else{						$nh=$nHar;					};				}			?>			<tr>				<td class="text-center"><img class="img-avatar img-avatar32" src="<?=$avatarm;?>" alt=""></td>				<td>					<?=$siswa['nama'];?>				</td>				<td>					<?=$nHar;?>				</td>				<td>					Kelebihan : <?=$kelebihan;?><br/>					Kelemahan : <?=$kelemahan;?>				</td>				<td>					<?=$nh;?>				</td>			</tr>			<?php 			}			?>                          </tbody>    </table><?php };
